import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { DataService } from '../service/data.service';
import { DatePipe } from '@angular/common';

@Component({
  selector: 'app-meeting',
  templateUrl: './meeting.component.html',
  styleUrls: ['./meeting.component.css']
})
export class MeetingComponent {
  meeting_message = "";

  meetingRef = new FormGroup({
    meet_topic:new FormControl(),
    meet_num_people:new FormControl(),
    meet_time:new FormControl()
  });

  constructor(public ds:DataService, public router) {} // DI for Data Service

  addMeeting(): void {
    // creating new meeting variables
    let new_meeting = this.meetingRef.value;
    this.ds.addMeeting(new_meeting);
    console.log(new_meeting);

  }

  displayMeeting(): void {
    let user = sessionStorage.getItem("emailid");
    let new_meeting = this.meetingRef.value;

    let readable_time = "test test test test test";
    console.log(readable_time);

    readable_time = new_meeting.meet_time;
    console.log(readable_time);

    readable_time.toLocaleString();
    console.log(readable_time);

    this.meeting_message = "Meeting created for " + user + ": " + readable_time;
    console.log(this.meeting_message);
  }
}
