import { Component } from '@angular/core';

@Component({
  selector: 'app-meeting',
  templateUrl: './meeting.component.html',
  styleUrls: ['./meeting.component.css']
})
export class MeetingComponent {

}
<div>
    <h3>Schedule a Meeting</h3>
    <hr>
    <form [formGroup]="meetingRef" (ngSubmit)="addMeeting()">
        <label>Meeting Topic</label><br/>
            <input type="Enter Meeting Topic" formControlName="meet_topic" id="topic"><br/>
        <label>Number of People</label><br/>
            <input type="Enter Number of People" formControlName="meet_num_people" id="num_people"><br/>
        <label>Start Time</label><br/>
            <input type="datetime-local" formControlName="meet_time" >

    </form>
</div>
