import { Component } from '@angular/core';
// import { LoginService } from '../login.service';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  msg:string = "";
  loginRef = new FormGroup({
    emailid:new FormControl(),
    password:new FormControl()
  });

  constructor(public router:Router) { // DI for Router API

  }

  checkLoginDetails(): void {
    let login = this.loginRef.value;

    if (login.emailid == "admin@gmail.com" && login.password == "admin123") {
      alert("Successful Login!");
      sessionStorage.setItem("emailid", login.emailid);
      this.router.navigate(["/home"], {skipLocationChange:true});
    }
    else {
      alert("Unsuccessful login attempt! Please try again!");
    }
    this.loginRef.reset();
  }

}
<div>
    <h1>Login Page for Meeting Scheduling App</h1>
    <form [formGroup]="loginRef" (ngSubmit)="checkLoginDetails()">
        <label>Email</label>
            <input type="email" name="emailid" id="email"/><br/>
        <label>Password</label>
            <input type="password" name="password" id="password"/><br/>
        <input type="submit" value="Login" id/>
        <input type="reset" value="Reset"/>
    </form>
</div>
