import { Component } from '@angular/core';
import { DataService } from '../service/data.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent {
  client_info = "";
  meeting_array:Array<any> = [];
  login_info = "";
  client_array:Array<any> = [];

  constructor(public router:Router, public ds:DataService) { }

  logout(): void {
    this.router.navigate(["/login"], {skipLocationChange:true});
  }

  displayAllClients(): void {
    this.client_array = this.ds.getAllClient();
  }

  displayAllLogins(): void {
    let logins_array = this.ds.getAllLogins();
    for (const key of logins_array) {
      this.login_info = this.login_info + logins_array[key].id + " ";
    }
    console.log(this.login_info);
  }

  displayAllMeetings(): void {
    this.meeting_array = this.ds.getAllMeetingInfo();
    console.log(this.meeting_array);
  }
}  
<div>
    <h2>Welcome to your Dashboard, Admin</h2>
    <hr>
    <input type="button" value="View All Clients" (click)="displayAllClients()"/> | 
    <input type="button" value="View All Meetings" (click)="displayAllMeetings()"/> |
    <input type="button" value="View All Logins" (click)="displayAllLogins();"/> | 
    <input type="button" value="log out" (click)="logout()"/>
    <!-- <h4>Client Info:</h4>
        <p>{{client_info}}</p>
    <h4>Meeting Info:</h4>
        <p>{{meeting_info}}</p>
    <h4>Logins Info:</h4>
        <p>{{login_info}}</p> -->

    <div *ngFor="let client of client_array">
        <p>client info: {{client.cli_name}} meeting info: {{client.meeting_info}} login info: {{client.login_info}}</p>
    </div>
</div>
